import { useState } from "react";
import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarHeader,
} from "@/components/ui/sidebar";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Separator } from "@/components/ui/separator";
import { 
  Activity, 
  Search, 
  Settings, 
  RefreshCw, 
  Wifi,
  WifiOff,
  ArrowUpDown,
  Trash2,
  MoreHorizontal
} from "lucide-react";
import { Tooltip, TooltipContent, TooltipTrigger } from "@/components/ui/tooltip";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import StatusIndicator from "./StatusIndicator";
import type { PLC } from "@shared/schema";

interface AppSidebarProps {
  plcs: PLC[];
  isLoading?: boolean;
  selectedPLCs: Set<string>;
  onConnect: (plcId: string) => void;
  onDisconnect: (plcId: string) => void;
  onRefresh: (plcId: string) => void;
  onConfigure: (plcId: string) => void;
  onDelete: (plcId: string) => void;
}

export function AppSidebar({ 
  plcs, 
  isLoading = false,
  selectedPLCs, 
  onConnect, 
  onDisconnect, 
  onRefresh, 
  onConfigure,
  onDelete
}: AppSidebarProps) {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOrder, setSortOrder] = useState<"asc" | "desc">("asc");

  const filteredAndSortedPLCs = plcs
    .filter(plc => 
      plc.plc_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (plc.plc_no?.toString() || '').includes(searchTerm) ||
      plc.plc_ip.includes(searchTerm)
    )
    .sort((a, b) => {
      const aNum = a.plc_no || 0;
      const bNum = b.plc_no || 0;
      const comparison = sortOrder === "asc" ? aNum - bNum : bNum - aNum;
      return comparison;
    });


  const connectedCount = plcs.filter(plc => selectedPLCs.has(plc.id)).length;
  const activeCount = plcs.filter(plc => plc.status === "active").length;
  const errorCount = plcs.filter(plc => plc.status === "error").length;

  const handleToggleConnection = (plc: PLC) => {
    if (selectedPLCs.has(plc.id)) {
      onDisconnect(plc.id);
    } else {
      onConnect(plc.id);
    }
  };

  const truncateName = (name: string) => {
    return name.length > 16 ? `${name.substring(0, 16)}...` : name;
  };

  return (
    <Sidebar>
      <SidebarHeader className="p-4">
        <div className="space-y-4">
          <div className="flex items-center gap-2">
            <Activity className="h-5 w-5 text-primary" />
            <span className="font-semibold text-lg">PLC Monitor</span>
          </div>

          <div className="grid grid-cols-3 gap-2 text-xs">
            <div className="text-center p-2 rounded-md bg-card">
              <div className="font-mono font-semibold text-green-600">{connectedCount}</div>
              <div className="text-muted-foreground">Connected</div>
            </div>
            <div className="text-center p-2 rounded-md bg-card">
              <div className="font-mono font-semibold text-blue-600">{activeCount}</div>
              <div className="text-muted-foreground">Active</div>
            </div>
            <div className="text-center p-2 rounded-md bg-card">
              <div className="font-mono font-semibold text-red-600">{errorCount}</div>
              <div className="text-muted-foreground">Errors</div>
            </div>
          </div>
        </div>
      </SidebarHeader>

      <SidebarContent>
        <SidebarGroup>
          <SidebarGroupLabel className="flex items-center justify-between">
            <span>PLCs ({filteredAndSortedPLCs.length})</span>
            <Button 
              variant="ghost" 
              size="sm"
              onClick={() => setSortOrder(prev => prev === "asc" ? "desc" : "asc")}
              data-testid="button-sort-sidebar"
            >
              <ArrowUpDown className="h-3 w-3" />
            </Button>
          </SidebarGroupLabel>

          <div className="px-2 pb-2">
            <div className="relative">
              <Search className="absolute left-2 top-1/2 transform -translate-y-1/2 h-3 w-3 text-muted-foreground" />
              <Input
                placeholder="Search..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-7 h-8 text-xs"
                data-testid="input-search-sidebar"
              />
            </div>
          </div>

          <SidebarGroupContent>
            <ScrollArea className="h-[calc(100vh-280px)]">
              <SidebarMenu>
                {filteredAndSortedPLCs.map((plc) => {
                  const isConnected = selectedPLCs.has(plc.id);
                  const displayName = `P_${plc.plc_no}_${truncateName(plc.plc_name)}`;
                  const fullName = `P_${plc.plc_no}_${plc.plc_name}`;

                  return (
                    <SidebarMenuItem key={plc.id}>
                      <Tooltip>
                        <TooltipTrigger asChild>
                          <div className="w-full">
                            <SidebarMenuButton 
                              className={`w-full justify-between p-3 h-auto ${
                                isConnected ? 'bg-sidebar-accent' : ''
                              }`}
                              onClick={() => handleToggleConnection(plc)}
                              data-testid={`sidebar-plc-${plc.id}`}
                            >
                              <div className="flex items-center gap-2 min-w-0 flex-1">
                                <StatusIndicator status={plc.status} size="sm" />
                                <div className="min-w-0 flex-1">
                                  <p className="font-mono text-xs font-medium truncate">
                                    {displayName}
                                  </p>
                                  <p className="font-mono text-xs text-muted-foreground">
                                    {plc.plc_ip}
                                  </p>
                                </div>
                              </div>

                              <div className="flex items-center gap-1 shrink-0">
                                {isConnected ? (
                                  <Wifi className={`h-3 w-3 ${
                                    plc.status === 'maintenance' ? 'text-yellow-600' : 'text-green-600'
                                  }`} />
                                ) : (
                                  <WifiOff className="h-3 w-3 text-muted-foreground" />
                                )}
                              </div>
                            </SidebarMenuButton>

                            {isConnected && (
                              <div className="px-3 pb-2">
                                <Separator className="mb-2" />
                                <div className="flex gap-1">
                                  <Button 
                                    variant="ghost" 
                                    size="sm" 
                                    className="h-6 text-xs"
                                    onClick={(e) => {
                                      e.stopPropagation();
                                      onRefresh(plc.id);
                                    }}
                                    data-testid={`button-refresh-sidebar-${plc.id}`}
                                  >
                                    <RefreshCw className="h-3 w-3" />
                                  </Button>
                                  <DropdownMenu>
                                    <DropdownMenuTrigger asChild>
                                      <Button 
                                        variant="ghost" 
                                        size="sm" 
                                        className="h-6 text-xs"
                                        onClick={(e) => {
                                          e.stopPropagation();
                                        }}
                                        data-testid={`button-configure-sidebar-${plc.id}`}
                                      >
                                        <MoreHorizontal className="h-3 w-3" />
                                      </Button>
                                    </DropdownMenuTrigger>
                                    <DropdownMenuContent align="end" className="w-40">
                                      <DropdownMenuItem
                                        onClick={(e) => {
                                          e.stopPropagation();
                                          onConfigure(plc.id);
                                        }}
                                        data-testid={`menu-configure-${plc.id}`}
                                      >
                                        <Settings className="h-3 w-3 mr-2" />
                                        Configure
                                      </DropdownMenuItem>
                                      <DropdownMenuItem
                                        onClick={(e) => {
                                          e.stopPropagation();
                                          onDelete(plc.id);
                                        }}
                                        className="text-red-600 focus:text-red-600"
                                        data-testid={`menu-delete-${plc.id}`}
                                      >
                                        <Trash2 className="h-3 w-3 mr-2" />
                                        Delete PLC
                                      </DropdownMenuItem>
                                    </DropdownMenuContent>
                                  </DropdownMenu>
                                </div>
                              </div>
                            )}
                          </div>
                        </TooltipTrigger>
                        {plc.plc_name.length > 16 && (
                          <TooltipContent side="right">
                            <p className="font-mono text-xs">{fullName}</p>
                            <p className="font-mono text-xs text-muted-foreground">
                              {plc.opcua_url}
                            </p>
                          </TooltipContent>
                        )}
                      </Tooltip>
                    </SidebarMenuItem>
                  );
                })}
              </SidebarMenu>

              {isLoading ? (
                <div className="text-center py-8 text-muted-foreground">
                  <p className="text-xs">Loading PLCs...</p>
                </div>
              ) : filteredAndSortedPLCs.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  <p className="text-xs">No PLCs found</p>
                  <p className="text-xs mt-1">
                    {searchTerm ? "Try different search terms" : "Add new PLCs to get started"}
                  </p>
                </div>
              ) : null}
            </ScrollArea>
          </SidebarGroupContent>
        </SidebarGroup>
      </SidebarContent>
    </Sidebar>
  );
}

client/src/components/Dashboard.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import EnhancedVariablesTable from "./EnhancedVariablesTable";
import { useLanguage } from "@/contexts/LanguageContext";
import { api } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";
import { type NodeData, type PLC } from "@shared/schema";
import { type NormalizedPLC, type NormalizedVariable } from "@shared/normalization";

export default function Dashboard() {
  const [nodeData, setNodeData] = useState<NodeData[]>([]);

  const [location] = useLocation();
  const queryClient = useQueryClient();
  const { toast } = useToast();
  const { t } = useLanguage();

  // Parse plcId from query parameters
  const searchParams = new URLSearchParams(location.split('?')[1] || '');
  const selectedPlcId = searchParams.get('plcId');

  // Fetch PLCs
  const { data: plcs = [], isLoading } = useQuery({
    queryKey: ["/api/plcs"],
    queryFn: api.getAllPLCs,
  });

  const connectedPLCs = plcs.filter(plc => plc.is_connected);

  // Select PLC: either from URL param or first connected PLC
  const selectedPLC = selectedPlcId 
    ? plcs.find(p => p.id === selectedPlcId) 
    : connectedPLCs.length > 0 ? connectedPLCs[0] : null;

  // Convert regular PLC to NormalizedPLC with mock variables for testing
  const createMockNormalizedPLC = (plc: PLC): NormalizedPLC => {
    const mockVariables: NormalizedVariable[] = [
      // Channel variable with children
      {
        id: `${plc.id}_ch1`,
        type: 'channel',
        plc_reg_add: 'D100',
        opcua_reg_add: 'ns=2;i=1001',
        description: 'Motor Control Status Register',
        data_type: 'channel',
        hasChildren: true,
      },
      // Child bit variables
      {
        id: `${plc.id}_ch1_bit0`,
        type: 'bool',
        plc_reg_add: 'D100.0',
        opcua_reg_add: 'ns=2;i=1001.0',
        description: 'Motor Running Status',
        data_type: 'bool',
        parentId: `${plc.id}_ch1`,
        bitPosition: 0,
      },
      {
        id: `${plc.id}_ch1_bit1`,
        type: 'bool',
        plc_reg_add: 'D100.1',
        opcua_reg_add: 'ns=2;i=1001.1',
        description: 'Motor Alarm Status',
        data_type: 'bool',
        parentId: `${plc.id}_ch1`,
        bitPosition: 1,
      },
      {
        id: `${plc.id}_ch1_bit2`,
        type: 'bool',
        plc_reg_add: 'D100.2',
        opcua_reg_add: 'ns=2;i=1001.2',
        description: 'Emergency Stop Active',
        data_type: 'bool',
        parentId: `${plc.id}_ch1`,
        bitPosition: 2,
      },
      // Another channel variable
      {
        id: `${plc.id}_ch2`,
        type: 'channel',
        plc_reg_add: 'D101',
        opcua_reg_add: 'ns=2;i=1002',
        description: 'Temperature Sensor Data',
        data_type: 'channel',
        hasChildren: true,
      },
      // Child variables for temperature
      {
        id: `${plc.id}_ch2_bit0`,
        type: 'bool',
        plc_reg_add: 'D101.0',
        opcua_reg_add: 'ns=2;i=1002.0',
        description: 'Temperature High Warning',
        data_type: 'bool',
        parentId: `${plc.id}_ch2`,
        bitPosition: 0,
      },
      {
        id: `${plc.id}_ch2_bit1`,
        type: 'bool',
        plc_reg_add: 'D101.1',
        opcua_reg_add: 'ns=2;i=1002.1',
        description: 'Temperature Critical Alert',
        data_type: 'bool',
        parentId: `${plc.id}_ch2`,
        bitPosition: 1,
      },
      // Standalone bool variables
      {
        id: `${plc.id}_bool1`,
        type: 'bool',
        plc_reg_add: 'M10',
        opcua_reg_add: 'ns=2;i=2001',
        description: 'Manual Override Enable',
        data_type: 'bool',
      },
      {
        id: `${plc.id}_bool2`,
        type: 'bool',
        plc_reg_add: 'M11',
        opcua_reg_add: 'ns=2;i=2002',
        description: 'Maintenance Mode Active',
        data_type: 'bool',
      },
    ];

    return {
      id: plc.id,
      plc_name: plc.plc_name,
      plc_ip: plc.plc_ip,
      opcua_url: plc.opcua_url,
      status: plc.status,
      last_checked: plc.last_checked,
      is_connected: plc.is_connected,
      created_at: plc.created_at,
      variables: mockVariables,
      registerCount: mockVariables.length,
      boolCount: mockVariables.filter(v => v.type === 'bool').length,
      channelCount: mockVariables.filter(v => v.type === 'channel').length,
    };
  };

  const normalizedSelectedPLC = selectedPLC ? createMockNormalizedPLC(selectedPLC) : null;

  // Debug logging (cleaned up for production)
  // console.log('Selected PLC:', selectedPLC);
  // console.log('Normalized PLC:', normalizedSelectedPLC);
  // console.log('Variables count:', normalizedSelectedPLC?.variables?.length || 0);

  const handleExportCSV = async () => {
    try {
      const blob = await api.exportCSV();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `node_data_${new Date().toISOString().split('T')[0]}.csv`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      toast({
        title: "Export Successful",
        description: "CSV file has been downloaded",
      });
    } catch (error) {
      toast({
        title: "Export Failed",
        description: "Failed to export CSV data",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="h-full flex flex-col">
      {selectedPLC && (
        <div className="p-6 border-b bg-muted/50">
          <div className="space-y-2">
            <h2 className="text-2xl font-bold" data-testid="text-plc-name">
              {selectedPLC.plc_name}
            </h2>
            <div className="flex items-center gap-6 text-sm text-muted-foreground">
              <span data-testid="text-plc-ip">
                <strong>IP:</strong> {selectedPLC.plc_ip}
              </span>
              <span data-testid="text-opcua-url">
                <strong>OPCUA URL:</strong> {selectedPLC.opcua_url}
              </span>
              <span data-testid="text-register-count">
                <strong>Registers:</strong> {normalizedSelectedPLC?.registerCount || 0}
              </span>
            </div>
          </div>
        </div>
      )}

      <div className="p-6 border-b">
        <div className="flex items-center justify-between">
          <h1 className="text-3xl font-bold" data-testid="text-dashboard-title">
            {selectedPLC ? `${selectedPLC.plc_name} Variables` : "OPC UA Dashboard"}
          </h1>
          <div className="text-right">
            <div className="text-sm text-muted-foreground" data-testid="text-plcs-connected">
              {connectedPLCs.length}/{plcs.length} PLCs Connected
            </div>
            <div className="text-sm text-muted-foreground" data-testid="text-last-updated">
              {t("lastUpdated")}: {new Date().toLocaleTimeString()}
            </div>
          </div>
        </div>
      </div>

      <div className="flex-1 overflow-hidden">
        {connectedPLCs.length > 0 || selectedPLC ? (
          <EnhancedVariablesTable 
            plc={normalizedSelectedPLC || undefined}
            onExportCSV={handleExportCSV}
            onRefresh={() => {
              queryClient.invalidateQueries({ queryKey: ["/api/plcs"] });
              toast({
                title: t("success"),
                description: t("dataRefreshed"),
              });
            }}
          />
        ) : (
          <div className="text-center py-24 text-muted-foreground">
            <div className="space-y-4">
              <h3 className="text-xl font-medium">
                {isLoading ? "Loading PLCs..." : plcs.length === 0 ? "No PLCs Available" : "No PLCs Connected"}
              </h3>
              <p className="text-sm max-w-md mx-auto">
                {isLoading ? "Please wait while we load your PLC configurations..." :
                 plcs.length === 0 ? "Add new PLC configurations using the \"Add New\" button to get started." :
                 "Connect to one or more PLCs from the sidebar to start monitoring live data."}
              </p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/JsonUploader.tsx.content:
import { useState, useCallback } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Upload, FileText, CheckCircle, AlertCircle, X } from "lucide-react";
import { type PLCConfig, type RawJSONData, rawJSONSchema } from "@shared/schema";
import { normalizePLCConfig } from "@shared/normalization";
import { api } from "@/lib/api";

interface JsonUploaderProps {
  onConfigUploaded: (config: PLCConfig) => void;
  onClose?: () => void;
}

export default function JsonUploader({ onConfigUploaded, onClose }: JsonUploaderProps) {
  const [isDragOver, setIsDragOver] = useState(false);
  const [uploadedConfig, setUploadedConfig] = useState<PLCConfig | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isProcessing, setIsProcessing] = useState(false);

  const processFile = useCallback(async (file: File) => {
    setIsProcessing(true);
    setError(null);

    try {
      const text = await file.text();
      const json = JSON.parse(text);
      const validatedData = rawJSONSchema.parse(json);
      const normalizedPLCs = normalizePLCConfig(validatedData);

      // For now, take the first PLC for single config handling
      const firstNormalizedPLC = normalizedPLCs[0];
      if (firstNormalizedPLC) {
        const apiConfig: PLCConfig = {
          plc_name: firstNormalizedPLC.plc_name,
          // Only include plc_no if it's a positive number, otherwise omit it (it's optional)
          ...(firstNormalizedPLC.plc_no && firstNormalizedPLC.plc_no > 0 ? { plc_no: firstNormalizedPLC.plc_no } : {}),
          plc_ip: firstNormalizedPLC.plc_ip,
          opcua_url: firstNormalizedPLC.opcua_url,
          address_mappings: firstNormalizedPLC.variables
            .filter(v => !v.parentId) // Only parent variables
            .map(v => ({
              node_name: v.opcua_reg_add,
              node_id: v.id,
              description: v.description || undefined,
              data_type: v.data_type || undefined,
            })),
        };

        setUploadedConfig(apiConfig);
        console.log('JSON config validated and normalized successfully:', apiConfig);
      }
    } catch (err) {
      if (err instanceof Error) {
        setError(err.message);
      } else {
        setError('Failed to process file');
      }
    } finally {
      setIsProcessing(false);
    }
  }, []);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);

    const files = Array.from(e.dataTransfer.files);
    const jsonFile = files.find(file => file.type === 'application/json' || file.name.endsWith('.json'));

    if (jsonFile) {
      processFile(jsonFile);
    } else {
      setError('Please upload a valid JSON file');
    }
  }, [processFile]);

  const handleFileInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      processFile(file);
    }
  };

  const handleAddToList = () => {
    if (uploadedConfig) {
      onConfigUploaded(uploadedConfig);
      setUploadedConfig(null);
      onClose?.();
    }
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = () => {
    setIsDragOver(false);
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-xl font-semibold">Upload PLC Configuration</h2>
        {onClose && (
          <Button variant="ghost" size="icon" onClick={onClose} data-testid="button-close-uploader">
            <X className="h-4 w-4" />
          </Button>
        )}
      </div>

      {!uploadedConfig && (
        <Card
          className={`border-2 border-dashed transition-colors cursor-pointer ${
            isDragOver 
              ? "border-primary bg-primary/5" 
              : "border-muted-foreground/25 hover:border-muted-foreground/50"
          }`}
          onDrop={handleDrop}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          data-testid="area-file-drop"
        >
          <CardContent className="p-8 text-center">
            <div className="space-y-4">
              {isProcessing ? (
                <>
                  <div className="animate-spin mx-auto">
                    <FileText className="h-12 w-12 text-muted-foreground" />
                  </div>
                  <p className="text-muted-foreground">Processing file...</p>
                </>
              ) : (
                <>
                  <Upload className="h-12 w-12 text-muted-foreground mx-auto" />
                  <div className="space-y-2">
                    <p className="text-lg font-medium">Drop your JSON file here</p>
                    <p className="text-muted-foreground">
                      or click to browse files
                    </p>
                  </div>

                  <input
                    type="file"
                    accept=".json,application/json"
                    onChange={handleFileInput}
                    className="hidden"
                    id="json-upload"
                    data-testid="input-file-upload"
                  />
                  <label htmlFor="json-upload">
                    <Button variant="outline" asChild>
                      <span>Browse Files</span>
                    </Button>
                  </label>
                </>
              )}
            </div>
          </CardContent>
        </Card>
      )}

      {error && (
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      {uploadedConfig && (
        <Card data-testid="card-config-preview">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <CheckCircle className="h-5 w-5 text-green-500" />
              Configuration Preview
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="text-sm font-medium text-muted-foreground">PLC Name</label>
                <p className="font-mono text-sm">{uploadedConfig.plc_name}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-muted-foreground">PLC Number</label>
                <p className="font-mono text-sm">{uploadedConfig.plc_no}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-muted-foreground">IP Address</label>
                <p className="font-mono text-sm">{uploadedConfig.plc_ip}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-muted-foreground">OPC UA URL</label>
                <p className="font-mono text-sm break-all">{uploadedConfig.opcua_url}</p>
              </div>
            </div>

            <Separator />

            <div>
              <label className="text-sm font-medium text-muted-foreground mb-2 block">
                Address Mappings ({uploadedConfig.address_mappings.length})
              </label>
              <div className="space-y-2 max-h-48 overflow-y-auto">
                {uploadedConfig.address_mappings.map((mapping, index) => (
                  <div key={index} className="border rounded-md p-3 text-sm">
                    <div className="flex items-center gap-2 mb-1">
                      <Badge variant="outline">{mapping.node_name}</Badge>
                      {mapping.data_type && (
                        <Badge variant="secondary">{mapping.data_type}</Badge>
                      )}
                    </div>
                    <p className="font-mono text-xs text-muted-foreground">{mapping.node_id}</p>
                    {mapping.description && (
                      <p className="text-xs text-muted-foreground mt-1">{mapping.description}</p>
                    )}
                  </div>
                ))}
              </div>
            </div>

            <div className="flex gap-2 pt-4">
              <Button onClick={handleAddToList} data-testid="button-add-to-list">
                Add to PLC List
              </Button>
              <Button variant="outline" onClick={() => setUploadedConfig(null)} data-testid="button-upload-another">
                Upload Another
              </Button>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

