import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Tooltip, TooltipContent, TooltipTrigger } from "@/components/ui/tooltip";
import { MoreVertical, Settings, RefreshCw, Wifi } from "lucide-react";
import { 
  DropdownMenu, 
  DropdownMenuContent, 
  DropdownMenuItem, 
  DropdownMenuTrigger 
} from "@/components/ui/dropdown-menu";
import StatusIndicator from "./StatusIndicator";
import type { PLC } from "@shared/schema";

interface PLCListItemProps {
  plc: PLC;
  onConnect?: (plcId: string) => void;
  onDisconnect?: (plcId: string) => void;
  onRefresh?: (plcId: string) => void;
  onConfigure?: (plcId: string) => void;
}

export default function PLCListItem({ 
  plc, 
  onConnect, 
  onDisconnect, 
  onRefresh, 
  onConfigure 
}: PLCListItemProps) {
  const [isConnecting, setIsConnecting] = useState(false);

  const handleConnect = async () => {
    if (plc.is_connected) {
      console.log(`Disconnecting PLC ${plc.plc_name}`);
      onDisconnect?.(plc.id);
    } else {
      console.log(`Connecting PLC ${plc.plc_name}`);
      setIsConnecting(true);
      // Simulate connection delay
      setTimeout(() => {
        setIsConnecting(false);
        onConnect?.(plc.id);
      }, 1000);
    }
  };

  const handleRefresh = () => {
    console.log(`Refreshing PLC ${plc.plc_name}`);
    onRefresh?.(plc.id);
  };

  const handleConfigure = () => {
    console.log(`Configuring PLC ${plc.plc_name}`);
    onConfigure?.(plc.id);
  };

  const truncatedName = plc.plc_name.length > 25 
    ? `${plc.plc_name.substring(0, 25)}...` 
    : plc.plc_name;

  return (
    <Card className="p-4 hover-elevate" data-testid={`card-plc-${plc.id}`}>
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-3 flex-1 min-w-0">
          <StatusIndicator status={plc.status} />

          <div className="flex-1 min-w-0">
            <Tooltip>
              <TooltipTrigger asChild>
                <div className="space-y-1">
                  <p className="font-mono text-sm font-medium text-foreground">
                    P_{plc.plc_no}_{truncatedName}
                  </p>
                  <p className="text-xs text-muted-foreground font-mono">
                    {plc.plc_ip}
                  </p>
                </div>
              </TooltipTrigger>
              {plc.plc_name.length > 25 && (
                <TooltipContent>
                  <p className="font-mono">P_{plc.plc_no}_{plc.plc_name}</p>
                </TooltipContent>
              )}
            </Tooltip>
          </div>

          <div className="flex items-center gap-2">
            <Badge variant={plc.is_connected ? "default" : "secondary"}>
              <Wifi className="w-3 h-3 mr-1" />
              {plc.is_connected ? "Connected" : "Disconnected"}
            </Badge>
          </div>
        </div>

        <div className="flex items-center gap-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={handleConnect}
            disabled={isConnecting}
            data-testid={`button-connect-${plc.id}`}
          >
            {isConnecting ? (
              <RefreshCw className="h-4 w-4 animate-spin" />
            ) : plc.is_connected ? (
              "Disconnect"
            ) : (
              "Connect"
            )}
          </Button>

          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="icon" data-testid={`button-menu-${plc.id}`}>
                <MoreVertical className="h-4 w-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuItem onClick={handleRefresh} data-testid={`menu-refresh-${plc.id}`}>
                <RefreshCw className="h-4 w-4 mr-2" />
                Refresh
              </DropdownMenuItem>
              <DropdownMenuItem onClick={handleConfigure} data-testid={`menu-configure-${plc.id}`}>
                <Settings className="h-4 w-4 mr-2" />
                Configure
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>

      <div className="mt-3 pt-3 border-t">
        <div className="flex items-center justify-between text-xs text-muted-foreground">
          <span className="font-mono">{plc.opcua_url}</span>
          <span>
            Last checked: {plc.last_checked.toLocaleTimeString()}
          </span>
        </div>
      </div>
    </Card>
  );
}

client/src/components/ServersPage.tsx.content:
import { useState } from "react";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Collapsible,
  CollapsibleContent,
  CollapsibleTrigger,
} from "@/components/ui/collapsible";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ChevronDown, ChevronRight, Server, Activity } from "lucide-react";
import { Link } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { api } from "@/lib/api";
import type { PLC } from "@shared/schema";

// Group PLCs by OPCUA URL
const groupPLCsByServer = (plcs: PLC[]) => {
  const grouped = new Map<string, PLC[]>();

  plcs.forEach(plc => {
    const serverUrl = plc.opcua_url;
    if (!grouped.has(serverUrl)) {
      grouped.set(serverUrl, []);
    }
    grouped.get(serverUrl)!.push(plc);
  });

  return Array.from(grouped.entries()).map(([url, plcs]) => ({
    serverUrl: url,
    plcs,
    connectedCount: plcs.filter(p => p.is_connected).length,
    totalCount: plcs.length,
    status: plcs.some(p => p.is_connected) ? "connected" : "disconnected",
    lastUpdated: Math.max(...plcs.map(p => new Date(p.last_checked ?? 0).getTime())),
  }));
};

export default function ServersPage() {
  const [expandedServers, setExpandedServers] = useState<Set<string>>(new Set());

  const { data: plcs = [], isLoading } = useQuery<PLC[]>({
    queryKey: ['api', 'plcs'],
  });

  const servers = groupPLCsByServer(plcs);
  const connectedServers = servers.filter(s => s.status === "connected").length;

  const toggleServerExpansion = (serverUrl: string) => {
    setExpandedServers(prev => {
      const newSet = new Set(prev);
      if (newSet.has(serverUrl)) {
        newSet.delete(serverUrl);
      } else {
        newSet.add(serverUrl);
      }
      return newSet;
    });
  };

  const getStatusBadge = (status: string) => {
    const variant = status === "connected" ? "default" : "secondary";
    const icon = status === "connected" ? Activity : Server;
    const Icon = icon;

    return (
      <Badge variant={variant} className="flex items-center gap-1" data-testid={`status-${status}`}>
        <Icon className="w-3 h-3" />
        {status === "connected" ? "Connected" : "Disconnected"}
      </Badge>
    );
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-2xl font-bold" data-testid="text-servers-title">OPCUA Servers</h1>
          <div className="text-sm text-muted-foreground" data-testid="text-servers-count">
            Loading servers...
          </div>
        </div>
        <div className="text-center py-24 text-muted-foreground">
          <p>Loading server configurations...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex items-center justify-between mb-6">
        <h1 className="text-2xl font-bold" data-testid="text-servers-title">OPCUA Servers</h1>
        <div className="text-sm text-muted-foreground" data-testid="text-servers-count">
          {connectedServers}/{servers.length} servers connected
        </div>
      </div>

      {servers.length === 0 ? (
        <div className="text-center py-24 text-muted-foreground">
          <div className="space-y-4">
            <h3 className="text-xl font-medium">No Servers Available</h3>
            <p className="text-sm max-w-md mx-auto">
              Add PLC configurations to see grouped servers here.
            </p>
          </div>
        </div>
      ) : (
        <div className="border rounded-lg">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead className="w-12"></TableHead>
                <TableHead data-testid="header-server-url">Server URL</TableHead>
                <TableHead data-testid="header-status">Status</TableHead>
                <TableHead data-testid="header-updated">Last Updated</TableHead>
                <TableHead data-testid="header-plc-count">PLCs Connected</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {servers.map((server) => {
                const isExpanded = expandedServers.has(server.serverUrl);

                return (
                  <Collapsible key={server.serverUrl} asChild>
                    <>
                      <CollapsibleTrigger asChild>
                        <TableRow 
                          className="cursor-pointer hover-elevate"
                          onClick={() => toggleServerExpansion(server.serverUrl)}
                          data-testid={`row-server-${server.serverUrl}`}
                        >
                          <TableCell>
                            {isExpanded ? (
                              <ChevronDown className="w-4 h-4" />
                            ) : (
                              <ChevronRight className="w-4 h-4" />
                            )}
                          </TableCell>
                          <TableCell className="font-medium" data-testid={`text-server-url-${server.serverUrl}`}>
                            {server.serverUrl}
                          </TableCell>
                          <TableCell data-testid={`status-server-${server.serverUrl}`}>
                            {getStatusBadge(server.status)}
                          </TableCell>
                          <TableCell data-testid={`text-updated-${server.serverUrl}`}>
                            {server.lastUpdated ? new Date(server.lastUpdated).toLocaleString() : "Never"}
                          </TableCell>
                          <TableCell data-testid={`text-plc-count-${server.serverUrl}`}>
                            {server.connectedCount}/{server.totalCount}
                          </TableCell>
                        </TableRow>
                      </CollapsibleTrigger>
                      <CollapsibleContent asChild>
                        <TableRow>
                          <TableCell></TableCell>
                          <TableCell colSpan={4}>
                            <div className="py-2 space-y-2">
                              <h4 className="font-medium text-sm text-muted-foreground mb-3">
                                PLCs on this server:
                              </h4>
                              <div className="grid gap-2">
                                {server.plcs.map((plc) => (
                                  <Link
                                    key={plc.id}
                                    href={`/?plcId=${plc.id}`}
                                    data-testid={`link-plc-${plc.id}`}
                                  >
                                    <div className="flex items-center justify-between p-3 border rounded hover-elevate">
                                      <div className="flex items-center gap-3">
                                        <div>
                                          <div className="font-medium" data-testid={`text-plc-name-${plc.id}`}>
                                            {plc.plc_name}
                                          </div>
                                          <div className="text-sm text-muted-foreground" data-testid={`text-plc-ip-${plc.id}`}>
                                            {plc.plc_ip}
                                          </div>
                                        </div>
                                      </div>
                                      <div className="flex items-center gap-2">
                                        {getStatusBadge(plc.is_connected ? "connected" : "disconnected")}
                                      </div>
                                    </div>
                                  </Link>
                                ))}
                              </div>
                            </div>
                          </TableCell>
                        </TableRow>
                      </CollapsibleContent>
                    </>
                  </Collapsible>
                );
              })}
            </TableBody>
          </Table>
        </div>
      )}
    </div>
  );
}

